import React, { Fragment, useRef, useState, useEffect } from "react";
import { connect } from "react-redux";
import { selectPhoto } from "../../../actions/gallery";

const MasonryItem = ({ gallery, selectPhoto }) => {
  const photo = useRef(null);
  const [selected, setSelected] = useState(null);

  useEffect(() => {
    setSelected(gallery.selected === photo);
    selected
      ? (photo.current.style.height = "500px")
      : (photo.current.style.height = "200px");
  }, [gallery.selected, selected]);

  return (
    <Fragment>
      {selected ? (
        <div></div>
      ) : (
        <div
          className='masonry-item'
          ref={photo}
          onClick={() => {
            selectPhoto(photo);
          }}
        ></div>
      )}
    </Fragment>
  );
};

const mapStateToProps = state => ({
  gallery: state.galleryReducer
});

export default connect(
  mapStateToProps,
  { selectPhoto }
)(MasonryItem);
