import React from "react";
import { connect } from "react-redux";
import { selectPhoto } from "../../../actions/gallery";

const MasonryItem = ({ gallery, selectPhoto }) => {
  // const ChangeStyle2 = e => {
  //   e.currentTarget.style.height = "500px";
  // };

  const highlightPhoto = e => {
    selectPhoto(e.currentTarget);
  };
  return (
    <div
      className='masonry-item'
      onClick={highlightPhoto}
      style={{ height: "200px" }}
    ></div>
  );
};

const mapStateToProps = state => ({
  gallery: state.galleryReducer
});

export default connect(
  mapStateToProps,
  { selectPhoto }
)(MasonryItem);
