import SizeSelectOptions from "./options/SizeSelectOptions";
import Select from "react-select";
// import MaterialSelectOptions from "./options/MaterialSelectOptions";
// import FrameSelectOptions from "./options/FrameSelectOptions";
// import BorderSelectOptions from "./options/BorderSelectOptions";
// import FinishSelectOptions from "./options/FinishSelectOptions";

const CustomiseProduct = () => {
  const [size, setSize] = useState("");
  // const [material, setMaterial] = useState("");
  // const [frame, setFrame] = useState("");
  // const [border, setBorder] = useState("");
  // const [finish, setFinish] = useState("");

  const customStyles = {
    option: (provided, state) => ({
      ...provided,
      borderBottom: "1px dotted pink",
      color: state.isSelected ? "red" : "blue",
      padding: 20
    }),
    control: () => ({
      // none of react-select's styles are passed to <Control />
      width: 200
    }),
    singleValue: (provided, state) => {
      const opacity = state.isDisabled ? 0.5 : 1;
      const transition = "opacity 300ms";

      return { ...provided, opacity, transition };
    }
  };

  return (
    <div className='customiseProduct-container'>
      <div className='customiseProduct-header'>
        <div className='customiseProduct-title'>Customise</div>
        <div className='customiseProduct-number'>#11</div>
      </div>
      <div className='customiseProduct-dropdown-containers'>
        <div className='dropdown-item'>
          <Select
            value={size}
            placeholder='Yay'
            styles={customStyles}
            options={[
              { value: "big", label: "big" },
              { value: "medium", label: "medium" },
              { value: "small", label: "small" }
            ]}
            onChange={e => setSize(e.target.value)}
          />
        </div>
