            name='border'
            value={border}
            className='customise-dropdown'
            onChange={e => setBorder(e.target.value)}
          >
            <option value='' disabled>
              Border
            </option>
            <BorderSelectOptions />
          </select>
        </div>

        <div className='break'></div>

        <div className='dropdown-item'>
          <select
            name='finish'
            value={finish}
            className='customise-dropdown'
            onChange={e => setFinish(e.target.value)}
          >
