import React, { useRef, useState, useEffect } from "react";
import { connect } from "react-redux";
import { selectPhoto } from "../../../actions/gallery";

const MasonryItem = ({ gallery, selectPhoto }) => {
  const photo = useRef(null);
  const [active, setActive] = useState(gallery.selected === photo);

  useEffect(() => {
    active
      ? (photo.current.style.height = "500px")
      : (photo.current.style.height = "200px");
  }, [active]);

  return (
    <div
      className='masonry-item'
      onClick={() => {
        selectPhoto(photo);
      }}
      ref={photo}
    ></div>
  );
};

const mapStateToProps = state => ({
  gallery: state.galleryReducer
});

export default connect(
  mapStateToProps,
  { selectPhoto }
)(MasonryItem);
