import React, { useState } from "react";
import SizeSelectOptions from "./options/SizeSelectOptions";
import Select from "react-select";
// import MaterialSelectOptions from "./options/MaterialSelectOptions";
// import FrameSelectOptions from "./options/FrameSelectOptions";
// import BorderSelectOptions from "./options/BorderSelectOptions";
// import FinishSelectOptions from "./options/FinishSelectOptions";

const CustomiseProduct = () => {
  const [size, setSize] = useState("");
  // const [material, setMaterial] = useState("");
  // const [frame, setFrame] = useState("");
  // const [border, setBorder] = useState("");
  // const [finish, setFinish] = useState("");

  const customStyles = {
    control: (base, state) => ({
      ...base,
      width: "9rem",
      height: "3rem",
      background: "#1a1a1a",
      borderRadius: state.isFocused ? "0" : "0",
      borderColor: state.isFocused ? "none" : "none",
      boxShadow: state.isFocused ? null : null,
      "&:hover": {
        borderColor: state.isFocused ? "none" : "none"
      }
    }),
    placeholder: (base, state) => ({
      ...base,
      color: "white",
      fontSize: "12pt",
      textTransform: "uppercase",
      paddingLeft: "1rem"
    }),
    indicatorSeparator: (base, state) => ({
      ...base,
      display: "none"
    }),
    dropdownIndicator: (base, state) => ({
      ...base,
      marginRight: "1rem"
    }),

    menu: base => ({
      ...base,
      // override border radius to match the box
      borderRadius: 0,
      // beautify the word cut by adding a dash see https://caniuse.com/#search=hyphens for the compatibility
      hyphens: "auto",
      // kill the gap
      marginTop: 0,
      textAlign: "left",
      // prevent menu to scroll y
      wordWrap: "break-word"
    }),
    menuList: base => ({
      ...base,
      // kill the white space on first and last option
      padding: 0
    })
  };

  return (
    <div className='customiseProduct-container'>
      <div className='customiseProduct-header'>
        <div className='customiseProduct-title'>Customise</div>
        <div className='customiseProduct-number'>#11</div>
      </div>
      <div className='customiseProduct-dropdown-containers'>
        <div className='dropdown-item'>
          <Select
            value={size}
            placeholder='Yay'
            styles={customStyles}
            options={[
              { value: "big", label: "big" },
              { value: "medium", label: "medium" },
              { value: "small", label: "small" }
            ]}
            onChange={e => setSize(e.target.value)}
          />
        </div>
        {/* 
        <div className='break'></div>

        <div className='dropdown-item'>
          <select
            name='material'
            value={material}
            className='customise-dropdown'
            onChange={e => setMaterial(e.target.value)}
          >
            <option value='' disabled>
              Material
            </option>
            <MaterialSelectOptions />
          </select>
        </div>

        <div className='break'></div>

        <div className='dropdown-item'>
          <select
            name='frame'
            value={frame}
            className='customise-dropdown'
            onChange={e => setFrame(e.target.value)}
          >
            <option value='' disabled>
              Frame
            </option>
            <FrameSelectOptions />
          </select>
        </div>

        <div className='break'></div>

        <div className='dropdown-item'>
          <select
            name='border'
            value={border}
            className='customise-dropdown'
            onChange={e => setBorder(e.target.value)}
          >
            <option value='' disabled>
              Border
            </option>
            <BorderSelectOptions />
          </select>
        </div>

        <div className='break'></div>

        <div className='dropdown-item'>
          <select
            name='finish'
            value={finish}
            className='customise-dropdown'
            onChange={e => setFinish(e.target.value)}
          >
            <option value='' disabled>
              Finish
            </option>
            <FinishSelectOptions />
          </select>
        </div> */}
      </div>
    </div>
  );
};

export default CustomiseProduct;
