import React, { useRef, useEffect, Fragment } from "react";
import { connect } from "react-redux";
import SideBar from "../layout/Sidebar";
import ArtistItem from "../layout/artist100/ArtistItem";
import Header from "../layout/Header";
import LocomotiveScroll from "locomotive-scroll";

const Artist100 = ({ auth }) => {
  const scrollRef = useRef(null);

  useEffect(() => {
    // eslint-disable-next-line
    if (scrollRef.current) {
      const scroll = new LocomotiveScroll({
        el: scrollRef.current,
        smooth: true,
        inertia: 400
      });
    }
  });

  if (auth.loading) {
    return <h2 className='loading'>Loading...</h2>;
  }

  return (
    <Fragment>
      <SideBar />
      <div data-scroll className='main-container'>
        <div
          data-scroll
          data-scroll-section
          className='main-content-container'
          ref={scrollRef}
        >
          <Header subtitle='Top 100' title='Artist 100' />
          <div data-scroll data-scroll-section id='artist100-container'>
            <ArtistItem />
            <ArtistItem />
            <ArtistItem />
            <ArtistItem />
            <ArtistItem />
            <ArtistItem />
            <ArtistItem />
            <ArtistItem />
            <ArtistItem />
            <ArtistItem />
            <ArtistItem />
            <ArtistItem />
          </div>
        </div>
      </div>
    </Fragment>
  );
};

const mapStateToProps = state => ({
  auth: state.authReducer
});

export default connect(mapStateToProps)(Artist100);
