import React, { useRef, useState, useEffect } from "react";
import { connect } from "react-redux";
import { selectPhoto } from "../../../actions/gallery";

const MasonryItem = ({ gallery, selectPhoto }) => {
  const photo = useRef(null);
  const [isSelected, setSelected] = useState(false);

  useEffect(() => {
    setSelected(gallery.selected === photo);
    isSelected
      ? (photo.current.style.height = "500px")
      : (photo.current.style.height = "200px");
  }, [gallery.selected, isSelected]);

  return (
    <div
      className='masonry-item'
      ref={photo}
      onClick={() => {
        selectPhoto(photo);
      }}
    >
      {isSelected && <div></div>}
    </div>
  );
};

const mapStateToProps = state => ({
  gallery: state.galleryReducer
});

export default connect(
  mapStateToProps,
  { selectPhoto }
)(MasonryItem);
