import React, { useState } from "react";
import { connect } from "react-redux";
import Select from "react-select";
import { customDropdownCSS } from "../CustomDropdownCSS";
import {
  setSize,
  setMaterial,
  setFrame,
  setBorder,
  setFinish
} from "../../../actions/product";

const CustomiseProduct = () => {
  const [size, setSize] = useState("");
  const [material, setMaterial] = useState("");
  const [frame, setFrame] = useState("");
  const [border, setBorder] = useState("");
  const [finish, setFinish] = useState("");

  return (
    <div className='customiseProduct-container'>
      <div className='customiseProduct-header'>
        <div className='customiseProduct-title'>Customise</div>
        <div className='customiseProduct-number'>#11</div>
      </div>
      <div className='customiseProduct-dropdown-containers'>
        <div className='dropdown-item'>
          <Select
            // value={size}
            placeholder='Size'
            styles={customDropdownCSS}
            options={[
              { value: "big", label: "big" },
              { value: "medium", label: "medium" },
              { value: "small", label: "small" }
            ]}
            onChange={e => setSize(e)}
          />
        </div>

        <div className='break'></div>

        <div className='dropdown-item'>
          <Select
            value={material}
            placeholder='Material'
            styles={customDropdownCSS}
            options={[
              { value: "big", label: "big" },
              { value: "medium", label: "medium" },
              { value: "small", label: "small" }
            ]}
            onChange={e => setMaterial(e)}
          />
        </div>

        <div className='break'></div>

        <div className='dropdown-item'>
          <Select
            value={frame}
            placeholder='Frame'
            styles={customDropdownCSS}
            options={[
              { value: "big", label: "big" },
              { value: "medium", label: "medium" },
              { value: "small", label: "small" }
            ]}
            onChange={e => setFrame(e)}
          />
        </div>

        <div className='break'></div>

        <div className='dropdown-item'>
          <Select
            value={border}
            placeholder='Border'
            styles={customDropdownCSS}
            options={[
              { value: "big", label: "big" },
              { value: "medium", label: "medium" },
              { value: "small", label: "small" }
            ]}
            onChange={e => setBorder(e)}
          />
        </div>

        <div className='break'></div>

        <div className='dropdown-item'>
          <Select
            value={finish}
            placeholder='Finish'
            styles={customDropdownCSS}
            options={[
              { value: "big", label: "big" },
              { value: "medium", label: "medium" },
              { value: "small", label: "small" }
            ]}
            onChange={e => setFinish(e)}
          />
        </div>
      </div>
    </div>
  );
};

const mapStateToProps = state => ({
  product: state.productReducer
});

export default connect(mapStateToProps)(CustomiseProduct);
