import React, { useRef, useEffect } from "react";
import { connect } from "react-redux";
import { selectPhoto } from "../../../actions/gallery";

const MasonryItem = ({ gallery, selectPhoto }) => {
  const photo = useRef(null);

  // const ChangeStyle2 = e => {
  //   e.currentTarget.style.height = "500px";
  // };

  useEffect(() => {
    if (gallery.selected === photo) {
      // photo.style.height = "500px";
    }
  }, [gallery.selected]);

  return (
    <div
      ref={photo}
      className='masonry-item'
      onClick={() => {
        selectPhoto(photo);
      }}
      style={{ height: "200px" }}
    ></div>
  );
};

const mapStateToProps = state => ({
  gallery: state.galleryReducer
});

export default connect(
  mapStateToProps,
  { selectPhoto }
)(MasonryItem);
